  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { BlogPost } from "../../components/templates/blog-post";
export const meta = {
  title: "Hello Next",
  summary: "Moving this site to Next.js",
  publishedAt: "2019-08-11",
  published: true,
  image: "",
  tags: ["next.js"]
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = BlogPost
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <h1>{`Hello Next`}</h1>
    <p>{`This is my first post on my new hand crafted blog!`}</p>
    <p>{`I'm going to keep this one short. Up until the time of this writing my site (and the one you are probably on)
has been using `}<a parentName="p" {...{
        "href": "https://www.gatsbyjs.org/"
      }}>{`Gatsby`}</a>{`. If you're a react developer and unfamiliar with Gatsby you should really know about it.
It's pretty awesome. Gatsby has served me well with its excellent dev tools and community for years but now I'm making
some changes to the site that will give it some cool new features for federation!`}</p>
    <p>{`Over the past year or so I've been interested in the `}<a parentName="p" {...{
        "href": "https://indieweb.org/"
      }}>{`Indie Web`}</a>{` and
`}<a parentName="p" {...{
        "href": "https://activitypub.rocks/"
      }}>{`ActivityPub`}</a>{`. I see it as a brave new internet that I would like to be a part of and help
build. So in order to have some extra server-side features I have moved this site over to `}<a parentName="p" {...{
        "href": "https://nextjs.org/"
      }}>{`Next.js`}</a>{`.`}</p>
    <p>{`I've been a big fan of Zeit since Next.js v1 but have never really delve into the framework so I'm really excited
to be finally getting my hands dirty with it.`}</p>
    <p>{`~ Jason`}</p>

    </MDXLayout>;
}

